import React, {Component} from 'react';

class User extends Component {

    state = {
        name: "gustavo torregrosa costa"
    }

    componentDidMount = () => {
        setTimeout(() => {
            this.setState({
                name: 'fefe'
            })       
        }, 2000)
    }

    getName = () => this.state.name

    setName = name => this.setState({name})

}

export default User
